import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { MensajeIdService } from '../mensaje-id.service';

@Component({
  selector: 'app-mensaje-id',
  templateUrl: './mensaje-id.component.html',
  styleUrls: ['./mensaje-id.component.css']
})
export class MensajeIdComponent {
  id: string = '';
  mensaje: any = null;
  errorMessage: string | null = null;

  constructor(
      private route: ActivatedRoute,
      private mensajeIdService: MensajeIdService
  ) {}

  ngOnInit(): void {
      // Obtener el ID de la URL
      this.id = this.route.snapshot.paramMap.get('id') as string;

      // Llamar al servicio para obtener el mensaje por ID
      this.mensajeIdService.obtenerMensajePorId(this.id)
          .subscribe(
              response => {
                  this.mensaje = response; // Asignar el mensaje
              },
              error => {
                  this.errorMessage = 'Error al obtener el mensaje'; // Asignar mensaje de error
              }
          );
  }
}
<div>
    <h2>Mensaje</h2>
    <div *ngIf="mensaje">
        <p><strong>Mensaje:</strong> {{ mensaje.msg }}</p>
        <p><strong>Etiquetas:</strong> {{ mensaje.tags.join(', ') }}</p>
    </div>
    <div *ngIf="errorMessage" class="alert alert-danger" role="alert">
        {{ errorMessage }}
    </div>
</div>
