import { NgModule } from '@angular/core';
import { BrowserModule } from '@angular/platform-browser';

import { RouterModule, Routes } from '@angular/router';
import { AppComponent } from './app.component';
import { HttpClientModule } from '@angular/common/http';
import { CredencialesComponent } from './credenciales/credenciales.component';
import { FormsModule } from '@angular/forms';
import { MensajeComponent } from './mensaje/mensaje.component';
import { MensajeService } from './mensaje.service';
import { MensajeIdComponent } from './mensaje-id/mensaje-id.component';
import { MensajeIdService } from './mensaje-id.service';

const routes: Routes = [
  { path: '', redirectTo: '/credenciales', pathMatch: 'full' },
  { path: 'credenciales', component: CredencialesComponent },
  { path: 'mensaje', component: MensajeComponent },
  { path: 'mensaje/:id', component: MensajeIdComponent },
  // Otras rutas si las tienes...
];

@NgModule({
  declarations: [
    AppComponent,
    CredencialesComponent,
    MensajeComponent,
    MensajeIdComponent
  ],
  imports: [
    BrowserModule,
    HttpClientModule,
    FormsModule,
    RouterModule.forRoot(routes)

  ],
  exports: [RouterModule],
  providers: [MensajeService,MensajeIdService],
  bootstrap: [AppComponent]
})
export class AppModule { }
